package UD04_act1;

public class UD04_act1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int tiempo = 2; //declarar y asignar valor a la variable tiempo
		int hora = 18; //declarar y asignar valor a la variable hora
		System.out.println("Resultado de sumar tiempo y hora " + hora + tiempo);//muestra texto y realiza suma
		System.out.println("Resultado de resta tiempo y hora "+ (hora - tiempo));// muestra texto y realiza resta
		System.out.println("Resultado de multiplicación tiempo y hora "+ hora * tiempo ); //muestra texto y realiza multiplicacion
		System.out.println("Resultado de división tiempo y hora "+ hora / tiempo );//muestra texto y realiza division
		System.out.println("Resultado de módulo tiempo y hora "+ hora % tiempo ); // muestra texto y realiza modulo 
		
	}

}
